import React, { Component } from 'react'
import TeamMember from './TeamMember'
import memberInfo from './MemberInfo'

class Team extends Component {
    constructor() {
        super()
        this.state = { memberInfo: memberInfo }
        this.state = { number: 0}
        this.clickHanlder = this.clickHanlder.bind(this);
    }
    mapping(x) {
        const members = x.map(member =>
        <TeamMember
        key={member.id}
        img={member.img}
        name={member.name}
        position={member.position}
        phone={member.phone}
        email={member.email}
        website={member.website}
      />);
        return members
    }

    clickHanlder() {
        this.setState(prevState => {
            return {
                number: prevState.number + 1
                
            }
           
        })
        console.log(memberInfo[number])
    }
    render() {
        return (
            <div className="row">
                <button className="btn btn-primary btn-lg btn-block"
                onClick={this.clickHanlder}>Click me!</button>
                {memberInfo[number]}   
            </div>
        )
    }
}

export default Team
