import React, { Component } from 'react'
import TeamMember from './TeamMember'
import memberInfo from './MemberInfo'

class TeamClick extends Component {
    constructor() {
        super()
        this.state = {
            memberInfo: memberInfo,
            members: [],
        }
      this.clickEvent = this.clickEvent.bind(this)
    }

    clickEvent() {
        this.state.members.push('test');
        console.log(this.state.members)
    }
    mapping(x) {
        const members = x.map(member =>
        <TeamMember
        key={member.id}
        img={member.img}
        name={member.name}
        position={member.position}
        phone={member.phone}
        email={member.email}
        website={member.website}
      />);
        return members
    }
    render() {
        return (
            <div className="row">
                <button className="btn btn-primary btn-lg btn-block"
                onClick={this.clickEvent}>Click me!</button>
                {this.mapping(this.state.memberInfo[this.state.counter])}   
            </div>
        )
    }
}

export default TeamClick
